package org.cmdbuild.utils.io.test;

import java.util.Arrays;
import javax.activation.DataHandler;
import org.apache.commons.codec.binary.Base64;
import org.apache.commons.io.FilenameUtils;
import static org.cmdbuild.utils.io.CmIoUtils.toByteArray;
import static org.cmdbuild.utils.io.CmIoUtils.toDataHandler;
import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNotNull;
import static org.junit.Assert.assertTrue;
import org.junit.Test;

public class DataHandlerTest {

	private final static byte[] PNG_FILE_DATA = Base64.decodeBase64(""),
			JPG_FILE_DATA = Base64.decodeBase64(""),
			TEXT_PLAIN_FILE_DATA = "hello, how are you?".getBytes();

	@Test
	public void testDataHandler1() {
		DataHandler dataHandler = toDataHandler(PNG_FILE_DATA);
		assertNotNull(dataHandler);
		assertEquals("image/png", dataHandler.getContentType());
		assertEquals("png", FilenameUtils.getExtension(dataHandler.getName()));
		assertTrue(Arrays.equals(PNG_FILE_DATA, toByteArray(dataHandler)));
	}

	@Test
	public void testDataHandler2() {
		DataHandler dataHandler = toDataHandler(JPG_FILE_DATA);
		assertNotNull(dataHandler);
		assertEquals("image/jpeg", dataHandler.getContentType());
		assertEquals("jpg", FilenameUtils.getExtension(dataHandler.getName()));
		assertTrue(Arrays.equals(JPG_FILE_DATA, toByteArray(dataHandler)));
	}

	@Test
	public void testDataHandler3() {
		DataHandler dataHandler = toDataHandler(TEXT_PLAIN_FILE_DATA);
		assertNotNull(dataHandler);
		assertEquals("text/plain", dataHandler.getContentType());
		assertEquals("txt", FilenameUtils.getExtension(dataHandler.getName()));
		assertTrue(Arrays.equals(TEXT_PLAIN_FILE_DATA, toByteArray(dataHandler)));
	}
        
}
